// Aardvark Model - Simple representation

// Parameters for size and shape
aardvark_length = 60;
aardvark_height = 30;
aardvark_width = 20;
snout_length = 30;
snout_radius = 5;
ear_size = 10;

// Body
module body() {
  hull() {
    translate([-aardvark_length/2, -aardvark_width/2, 0])
    sphere(r=aardvark_length/4);
    translate([aardvark_length/2, -aardvark_width/2, 0])
    sphere(r=aardvark_length/4);
    translate([aardvark_length/2, -aardvark_width/2, aardvark_height/2])
    sphere(r=aardvark_length/4);
    translate([-aardvark_length/2, -aardvark_width/2, aardvark_height/2])
    sphere(r=aardvark_length/4);
  }
}

// Snout
module snout() {
  cylinder(h=snout_length, r=snout_radius, center=false);
}

// Ear
module ear() {
  translate([0, 0, -ear_size/2])
  rotate([0, 0, 45])
  cube([ear_size, ear_size/2, ear_size], center=true);
}

// Legs (simplified - just cylinders)
module leg() {
  cylinder(h=aardvark_height/2, r=aardvark_width/4, center=false);
}

// Main assembly
module aardvark() {
  // Body
  body();

  // Snout
  translate([aardvark_length/2, 0, 0])
  snout();

  // Ears
  translate([-aardvark_length/2, -aardvark_width/2, aardvark_height/2])
  ear();

  translate([-aardvark_length/2, aardvark_width/2, aardvark_height/2])
  mirror([1,0,0])
  ear();

  // Legs - positioned roughly under the body
  translate([-aardvark_length/4, -aardvark_width/2, 0])
  leg();

  translate([aardvark_length/4, -aardvark_width/2, 0])
  leg();

  translate([-aardvark_length/4, aardvark_width/2, 0])
  leg();

  translate([aardvark_length/4, aardvark_width/2, 0])
  leg();
}

