// Aardwolf - medium complexity realistic model
// Overall size ~80mm long, 40mm tall

// Body dimensions
body_length = 60;
body_height = 35;
body_width = 25;

// Head dimensions
head_length = 20;
head_height = 15;
head_width = 15;

// Leg dimensions
leg_height = 20;
leg_width = 6;
leg_depth = 8;

// Tail dimensions
tail_length = 25;
tail_height = 8;
tail_width = 6;

// Ear dimensions
ear_height = 8;
ear_width = 4;
ear_depth = 2;

// Mane strip dimensions
mane_height = 3;
mane_width = 2;

// Main assembly
union() {
    // Central body
    translate([-body_length/2, 0, 0])
    scale([1, 0.8, 0.6])
    sphere(d=body_width);

    // Extended torso
    translate([-body_length/3, 0, body_height/4])
    cube([body_length*0.8, body_width*0.7, body_height*0.5], center=true);

    // Head
    translate([body_length/2 - 5, 0, body_height/2 + 5])
    rotate([0, -10, 0])
    union() {
        // Main head shape
        cube([head_length, head_width, head_height], center=true);

        // Snout extension
        translate([head_length/2 + 3, 0, -head_height/4])
        scale([1.5, 0.8, 0.6])
        sphere(d=head_width);

        // Nose
        translate([head_length/2 + 7, 0, -head_height/4])
        sphere(d=3);

        // Ears
        for(side = [-1, 1]) {
            translate([head_length/4, side*(head_width/2 + ear_width/2), head_height/2])
            rotate([0, side*10, 0])
            cube([ear_depth, ear_width, ear_height], center=true);
        }
    }

    // Legs
    for(side = [-1, 1]) {
        // Front legs
        translate([body_length/4, side*body_width/2, 0])
        union() {
            // Upper leg
            translate([0, 0, leg_height/2])
            cube([leg_depth, leg_width, leg_height], center=true);

            // Paw
            translate([0, 0, leg_height - 2])
            sphere(d=leg_width*1.2);
        }

        // Rear legs
        translate([-body_length/3, side*body_width/2, 0])
        union() {
            // Upper leg
            translate([0, 0, leg_height/2])
            cube([leg_depth*1.2, leg_width*1.1, leg_height*1.1], center=true);

            // Paw
            translate([0, 0, leg_height - 2])
            sphere(d=leg_width*1.2);
        }
    }

    // Tail
    translate([-body_length/2 - tail_length/2, 0, body_height/3])
    rotate([0, 0, -20])
    union() {
        // Tail base
        cube([tail_length, tail_width, tail_height], center=true);

        // Tail tip
        translate([-tail_length/2 - 2, 0, 0])
        sphere(d=tail_width);
    }

    // Mane along back
    for(i = [-body_length/2 + 5 : 5 : body_length/2 - 10]) {
        translate([i, 0, body_height/2 + mane_height/2])
        cube([mane_width, body_width*0.8, mane_height], center=true);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([body_length/2 + 2, side*head_width/3, body_height/2 + 8])
        sphere(d=2);
    }

    // Stripes on body
    for(i = [-body_length/3 : 8 : body_length/3]) {
        translate([i, 0, body_height/2 + 1])
        cube([2, body_width*0.9, 1], center=true);
    }
}

