// Parameters for overall size and shape
shell_length = 50;
shell_width = 30;
shell_height = 20;
shell_thickness = 3;
spiral_radius = shell_length / 2;
spiral_height = shell_height;

// Number of segments for the spiral
num_segments = 32;

// Function to create a segment of the spiral
module spiral_segment(angle, radius, height) {
    rotate([0, 0, angle])
    translate([radius, 0, 0])
    cube([shell_thickness, shell_width, height], center = true);
}

// Main module to create the abalone shell
module abalone_shell() {
    union() {
        // Create the spiral shape using multiple cubes
        for (i = [0:num_segments-1]) {
            angle = i * (360 / num_segments);
            radius = spiral_radius * (i / (num_segments -1));
            height = spiral_height * (i / (num_segments - 1));
            spiral_segment(angle, radius, height);
        }
    }
}

// Create the abalone shell and rotate for a more natural orientation
rotate([0, -30, 0])
abalone_shell();

