// Adder - A simple 3D model of an adder circuit

// Parameters for the adder's size and shape
adder_width = 20;
adder_height = 10;
adder_depth = 10;
input_pin_radius = 2;
output_pin_radius = 2;
pin_spacing = 5;
body_thickness = 2;

// Body of the adder
module adder_body() {
  cube([adder_width, adder_depth, adder_height], center = false);
}

// Input pin module
module input_pin(x_offset) {
  translate([x_offset, adder_depth/2, adder_height/2])
  cylinder(h = body_thickness, r = input_pin_radius);
}

// Output pin module
module output_pin(x_offset) {
  translate([x_offset, -adder_depth/2, adder_height/2])
  cylinder(h = body_thickness, r = output_pin_radius);
}

// Carry input pin
module carry_input_pin() {
  translate([adder_width/2 - pin_spacing, adder_depth/2, adder_height/2])
  cylinder(h = body_thickness, r = input_pin_radius);
}

// Carry output pin
module carry_output_pin() {
  translate([adder_width/2 + pin_spacing, -adder_depth/2, adder_height/2])
  cylinder(h = body_thickness, r = output_pin_radius);
}

// Create the adder model
module adder() {
  // Main body
  adder_body();

  // Input pins - A, B
  input_pin(-adder_width/2 + pin_spacing);
  input_pin(-adder_width/2 + 2 * pin_spacing);

  //Output pins - Sum, Carry
  output_pin(adder_width/2 - pin_spacing);
  output_pin(adder_width/2 - 2 * pin_spacing);

  // Carry Input/Output
  carry_input_pin();
  carry_output_pin();
}

// Render the adder
adder();

