// Body parameters
body_length = 50;
body_width = 20;
body_height = 15;
body_curve = 5;  // Adjust for body curvature

// Head parameters
head_length = 30;
head_width = 20;
head_height = 15;

// Fin parameters
fin_length = 20;
fin_height = 5;

// Lure parameters
lure_length = 10;
lure_width = 3;
lure_height = 3;

// Eye parameters
eye_radius = 3;

// Function to create a curved body segment
module curved_body_segment(length, width, height, curve) {
  hull() {
    translate([0, 0, 0]) sphere(r=width/2);
    translate([length, 0, 0]) sphere(r=width/2);
    translate([length/2, 0, height/2]) sphere(r=width/2);
    translate([length/2,0,-height/2]) sphere(r=width/2);

    translate([length/2,0,0])
      rotate([0,90,0])
        cylinder(h=height, r=width/2, $fn=20);
  }
}


// Body
module body() {
  difference() {
    curved_body_segment(body_length, body_width, body_height, body_curve);

    // Create a slight indentation for the mouth
    translate([body_length/2,0,0])
      cube([10, body_width*2, body_height*2], center=true);
  }
}

// Head
module head() {
  translate([body_length, 0, 0]) {
    rotate([0,90,0]) cylinder(h=head_length, r=head_width/2, $fn=20);
  }
}

// Fins
module fin() {
  translate([0, 0, 0]) {
    cube([fin_length, fin_height, 2], center=true);
  }
}

// Lure
module lure() {
  translate([0, 0, 0]) {
    cube([lure_length, lure_width, lure_height], center=true);
  }
}

// Eye
module eye() {
  sphere(r=eye_radius);
}

// Main assembly
module anglerfish() {

  // Body
  body();

  // Head
  head();

  // Left fin
  translate([body_length/2, body_width/2, body_height/2])
    rotate([0,0,90])
    fin();

  // Right fin
  translate([body_length/2, -body_width/2, body_height/2])
    rotate([0,0,-90])
    fin();

  // Lure & Stem
  translate([head_length+10, 0, 0])
    rotate([90,0,0])
      cylinder(h=lure_length, r=1.5, $fn=20);

  translate([head_length + lure_length/2 + 10,0,0])
    lure();

  // Eyes
  translate([head_length/2, body_width/3, body_height/2])
    eye();

  translate([head_length/2, -body_width/3, body_height/2])
    eye();
}

