// Anglerfish - realistic style, medium complexity
// Approx 80mm long

// Main parameters
body_length = 60;
body_height = 35;
head_radius = 20;
mouth_width = 30;
mouth_height = 15;
eye_size = 8;

// Body module
module body() {
    // Main body - ellipsoid shape
    scale([1.2, 0.9, 0.7])
        sphere(r=head_radius, $fn=50);

    // Belly bulge
    translate([0, 0, -5])
        scale([1, 1.2, 0.5])
            sphere(r=head_radius*0.9, $fn=40);

    // Tail section
    translate([-body_length/2, 0, 0])
        rotate([0, 15, 0])
            scale([1, 0.6, 0.4])
                cylinder(h=body_length/2, r=head_radius*0.8, $fn=40);
}

// Head module
module head() {
    // Upper head dome
    translate([head_radius/2, 0, 10])
        sphere(r=head_radius, $fn=50);

    // Lower jaw
    translate([head_radius/2, 0, -mouth_height/2])
        scale([1.2, 0.8, 0.5])
            sphere(r=mouth_width/2, $fn=40);
}

// Mouth module
module mouth() {
    // Mouth opening
    difference() {
        translate([head_radius+5, 0, 0])
            scale([1.5, 1, 0.6])
                sphere(r=mouth_width/2, $fn=40);

        // Teeth pattern
        for(i = [-10:5:10]) {
            for(j = [-5:5:5]) {
                translate([head_radius+mouth_width/2+2, i, j])
                    rotate([0, 90, 0])
                        cylinder(h=2, r=1.2, $fn=6);
            }
        }
    }
}

// Eye module
module eye(pos_x, pos_y) {
    // Eye socket
    translate([pos_x, pos_y, 5])
        sphere(r=eye_size/2+1, $fn=20);

    // Eyeball
    difference() {
        translate([pos_x, pos_y, 5])
            sphere(r=eye_size/2, $fn=20);

        // Pupil
        translate([pos_x+eye_size/3, pos_y, 5])
            sphere(r=eye_size/4, $fn=15);
    }
}

// Lure module (illicium)
module lure() {
    // Stalk
    translate([head_radius+10, 0, head_radius+10])
        rotate([0, 30, 0])
            cylinder(h=25, r=1.5, $fn=12);

    // Lure bulb
    translate([head_radius+30, 0, head_radius+20])
        sphere(r=4, $fn=20);

    // Lure details
    difference() {
        translate([head_radius+30, 0, head_radius+20])
            sphere(r=3.5, $fn=20);

        for(i = [0:60:300]) {
            rotate([0, 0, i])
                translate([head_radius+30+2, 0, head_radius+20])
                    cylinder(h=1, r=0.5, $fn=6);
        }
    }
}

// Fin module
module fin(pos_x, pos_y, angle) {
    translate([pos_x, pos_y, 0])
        rotate([0, 0, angle])
            scale([1, 0.2, 0.4])
                cylinder(h=15, r=8, $fn=20);
}

// Dorsal fin
module dorsal_fin() {
    translate([-10, 0, 20])
        rotate([15, 0, 0])
            scale([0.3, 1, 1])
                cylinder(h=15, r=10, $fn=30);
}

// Assemble the anglerfish
union() {
    // Main body parts
    body();
    head();
    mouth();

    // Eyes
    eye(head_radius/2, -8);
    eye(head_radius/2, 8);

    // Lure
    lure();

    // Fins
    fin(-5, -20, -30);
    fin(-5, 20, 30);

    // Dorsal fin
    dorsal_fin();

    // Pectoral fins
    translate([0, -25, -10])
        rotate([0, -20, -90])
            scale([0.3, 1, 0.5])
                cylinder(h=20, r=8, $fn=20);

    translate([0, 25, -10])
        rotate([0, 20, 90])
            scale([0.3, 1, 0.5])
                cylinder(h=20, r=8, $fn=20);
}

