// Anhinga (Anhinga anhinga) - Medium complexity realistic model
// 3D printable, ~80 mm long

$fn = 30; // Resolution for curved surfaces

// Main module
module anhinga() {
    union() {
        // Body
        body();

        // Neck and head
        translate([0, 0, 8]) rotate([0, 0, 25]) neck_head();

        // Tail
        translate([-32, 0, 2]) rotate([0, 0, -15]) tail();

        // Wings
        left_wing();
        mirror([0,1,0]) left_wing();

        // Legs and feet
        translate([20, -5, -4]) rotate([0, 90, 0]) right_leg();
        translate([20, 5, -4]) rotate([0, -90, 0]) left_leg();
    }
}

// Body module
module body() {
    // Main body - elongated oval
    scale([1.8, 0.6, 0.7]) sphere(d = 30);

    // Chest ridge
    translate([0, 0, 10]) scale([1.4, 0.4, 0.3]) sphere(d = 20);

    // Back ridge
    translate([5, 0, 8]) scale([1.5, 0.5, 0.2]) sphere(d = 25);
}

// Neck and head module
module neck_head() {
    // Neck - series of decreasing cylinders
    union() {
        translate([0, 0, 0]) cylinder(h = 25, d1 = 12, d2 = 8, center = false);
        translate([0, 0, 20]) cylinder(h = 20, d1 = 8, d2 = 6, center = false);

        // Head
        translate([0, 0, 42]) rotate([90, 0, -15]) {
            // Main head shape
            scale([1.3, 0.4, 0.5]) sphere(d = 12);

            // Beak
            translate([8, 0, 0]) rotate([0, 0, 15]) cylinder(h = 18, d1 = 4, d2 = 1.5, center = false);

            // Eye
            translate([4, 3, 2]) sphere(d = 2);
        }
    }
}

// Tail module
module tail() {
    // Fan tail made of individual feathers
    for (i = [-4:4]) {
        translate([i*2, 0, 0]) rotate([0, 0, i*8]) {
            scale([0.3, 1, 0.1]) cylinder(h = 25, d = 6);
        }
    }

    // Tail base
    translate([0, 0, 0]) scale([0.8, 0.3, 0.2]) sphere(d = 15);
}

// Left wing module
module left_wing() {
    translate([0, -15, 6]) rotate([0, 0, -25]) {
        // Upper wing
        scale([1.2, 0.2, 0.4]) sphere(d = 25);

        // Primary feathers
        for (i = [1:4]) {
            translate([-5 + i*2, -8 - i*1.5, 0]) rotate([0, 0, -15 + i*5]) {
                scale([0.8, 0.1, 0.05]) cylinder(h = 15, d = 3);
            }
        }

        // Secondary feathers
        for (j = [1:3]) {
            translate([-8 + j*2, -5 - j*1, 0]) rotate([0, 0, -10 + j*3]) {
                scale([0.7, 0.1, 0.05]) cylinder(h = 12, d = 2.5);
            }
        }
    }
}

// Leg module (reused for both legs)
module right_leg() {
    // Thigh
    translate([0, 0, 0]) cylinder(h = 8, d = 3);

    // Lower leg
    translate([0, 0, 8]) rotate([0, 0, -20]) cylinder(h = 10, d = 2.5);

    // Foot
    translate([0, 0, 18]) rotate([0, 0, 20]) {
        // Main foot
        scale([1.5, 0.3, 0.2]) sphere(d = 8);

        // Toes
        for (k = [0:3]) {
            translate([k*3, 0, 0]) rotate([0, 0, k*15 - 15]) {
                scale([0.3, 0.2, 0.1]) cylinder(h = 6, d = 2);
            }
        }
    }
}

// Scale for 3D printing
scale(0.8)
anhinga();
