// Aardvark Model in OpenSCAD

// Parameters for Aardvark Dimensions
body_length = 100;
body_width = 40;
body_height = 30;
ear_length = 25;
ear_width = 15;
ear_height = 20;
snout_length = 25;
snout_width = 20;
snout_height = 15;
leg_length = 40;
leg_radius = 5;
tail_length = 30;
tail_radius = 4;

// Function for creating a rounded cone
module rounded_cone(height, radius, rounds) {
  linear_extrude(height = height, center = false, convexity = 10) {
    difference() {
      circle(r = radius, $fn = 24);
      circle(r = radius * (1 - (1 / (rounds + 1))), $fn = 24);
    }
  }
}

// Body
module aardvark_body() {
  hull() {
    translate([0, 0, 0]) sphere(r = body_width / 2);
    translate([body_length, 0, 0]) sphere(r = body_width / 2);
  }

  // Flatten the body
  linear_extrude(height = body_height, center = false, convexity = 10) {
    difference() {
      hull() {
        translate([0, 0, 0]) sphere(r = body_width / 2);
        translate([body_length, 0, 0]) sphere(r = body_width / 2);
      }
      translate([0, 0, -1]) cube([body_length + 1, body_width + 1, body_height + 1]);
    }
  }
}

// Ears
module aardvark_ear() {
  translate([0, -ear_width/2, 0])
  rotate([0,0,20])
  sphere(r = ear_height/2);
}

// Snout
module aardvark_snout() {
  translate([body_length - snout_length/2, 0, 0])
  rotate([0,0, -10])
  linear_extrude(height = snout_height, center = false, convexity = 10) {
    circle(r = snout_width/2, $fn = 16);
  }
}

// Legs
module aardvark_leg() {
  cylinder(h = leg_length, r = leg_radius, $fn = 16);
}

// Tail
module aardvark_tail() {
  translate([body_length, 0, 0])
  rotate([90, 0, 0])
  cylinder(h = tail_length, r = tail_radius, $fn = 16);
}

// Main assembly
module aardvark() {
  // Body
  aardvark_body();

  // Ears
  translate([body_length / 2, body_width / 2, body_height])
  aardvark_ear();

  translate([body_length / 2, -body_width / 2, body_height])
  aardvark_ear();

  // Snout
  aardvark_snout();

  // Legs
  translate([body_length / 2 - body_width/3, -body_width/2 - leg_radius, 0])
  aardvark_leg();

  translate([body_length / 2 + body_width/3, -body_width/2 - leg_radius, 0])
  aardvark_leg();

  translate([body_length / 2 - body_width/3, body_width/2 - leg_radius, 0])
  aardvark_leg();

  translate([body_length / 2 + body_width/3, body_width/2 - leg_radius, 0])
  aardvark_leg();

  // Tail
  aardvark_tail();

}

// Render the aardvark
aardvark();

