// Abalone Shell Model in OpenSCAD

// Parameters for shell dimensions and features
shell_length = 100;
shell_width = 80;
shell_height = 25;
ridge_height = 8;
ridge_spacing = 15;
ridge_width = 10;
n_ridges = 10;
inner_radius = 20;
inner_height = 10;

// Function to create a single ridge
module ridge(height, width, spacing) {
  cube([width, spacing, height]);
}

// Main shell body
module abalone_shell() {
  difference() {
    // Main shell shape - ellipsoid
    scale([1, 1, shell_height / shell_width])
    sphere(r = shell_width / 2, $fn = 50);

    // Inner cavity - ellipsoid
    scale([1, 1, inner_height / inner_radius])
    translate([0, 0, -inner_height/2])
    sphere(r = inner_radius, $fn = 30);
  }
}

// Adding ridges to the shell
module abalone_ridges() {
  for (i = [0:n_ridges]) {
    angle = i * 360 / n_ridges;
    rotate([0, 0, angle])
    translate([shell_width / 2 + ridge_spacing, 0, shell_height / 2])
    ridge(ridge_height, ridge_width, 1);
  }
}

// Main Assembly
module abalone() {
  // Shell Body
  abalone_shell();

  // Ridges
  translate([0,0,-1]) // Slightly offset ridges for better visibility
  abalone_ridges();
}

// Render the abalone model
abalone();

