// Abalone shell with realistic features - medium complexity
// Size: ~70mm length, 3D printable with proper walls

// Shell thickness parameter
shell_t = 1.5;

// Main shell body module
module shell_body() {
    difference() {
        // Outer shell - elliptical dome shape
        scale([1, 0.7, 0.4])
        sphere(d=70, $fn=60);

        // Hollow interior
        translate([0, 0, -shell_t])
        scale([0.9, 0.6, 0.35])
        sphere(d=68, $fn=60);
    }
}

// Shell opening (mouth)
module shell_opening() {
    difference() {
        // Opening base
        translate([0, -30, -10])
        rotate([90, 0, 0])
        cylinder(h=20, d1=35, d2=25, $fn=40);

        // Taper the opening
        translate([0, -35, -10])
        rotate([90, 0, 0])
        cylinder(h=25, d1=30, d2=20, $fn=40);
    }
}

// Growth rings on shell surface
module growth_rings() {
    for (i = [1:12]) {
        rotate([0, 0, i * 15])
        translate([0, 0, -5 + i * 0.8])
        scale([1 - i * 0.03, 0.7 - i * 0.02, 0.4])
        sphere(d=70 - i * 2, $fn=60);
    }
}

// Spiral ridges following shell growth
module spiral_ridges() {
    for (a = [0:30:360]) {
        rotate([0, 0, a])
        translate([15, 0, -8])
        rotate([90, 0, 0])
        cylinder(h=30, d1=1.5, d2=0.5, $fn=8);
    }

    for (a = [15:30:360]) {
        rotate([0, 0, a])
        translate([25, 0, -5])
        rotate([90, 0, 0])
        cylinder(h=25, d1=1.2, d2=0.4, $fn=8);
    }
}

// Muscle attachment scars
module muscle_scars() {
    // Row of scars near opening
    for (pos = [[-20, -25, -8], [-10, -28, -8], [0, -30, -8], [10, -28, -8], [20, -25, -8]]) {
        translate(pos)
        scale([1.5, 1, 0.5])
        sphere(d=3, $fn=12);
    }
}

// Shell rim/lip
module shell_rim() {
    difference() {
        // Outer rim
        translate([0, -32, -12])
        rotate([90, 0, 0])
        cylinder(h=5, d=38, $fn=60);

        // Inner rim
        translate([0, -32, -12])
        rotate([90, 0, 0])
        cylinder(h=5, d=32, $fn=60);
    }
}

// Combine all parts
union() {
    // Main shell structure
    shell_body();

    // Add opening
    shell_opening();

    // Surface details
    growth_rings();

    // Spiral ridges
    spiral_ridges();

    // Muscle scars
    muscle_scars();

    // Shell rim
    shell_rim();
}

