// Agama Lizard Model - OpenSCAD

// Parameters - Adjust these to change the lizard's shape
lizard_length = 100;
lizard_width = 40;
head_radius = 20;
neck_length = 25;
body_height = 20;
tail_length = 50;
leg_length = 30;
leg_radius = 5;
foot_size = 15;

// Helper function to create a tapered cylinder
module tapered_cylinder(h, r1, r2) {
  linear_extrude(height = h)
  circle(r = r1);
}

// Head
module head() {
  sphere(r = head_radius);
}

// Neck
module neck() {
  tapered_cylinder(neck_length, head_radius, lizard_width / 2);
}

// Body
module body() {
  cube([lizard_length / 3, lizard_width, body_height]);
}

// Tail
module tail() {
  tapered_cylinder(tail_length, lizard_width / 2, 2);
}

// Leg
module leg() {
  cylinder(r = leg_radius, h = leg_length);
  translate([0,0,-foot_size/2])
  cube([leg_radius*3, leg_radius, foot_size]);
}

// Assemble the lizard
module agama() {

  // Head
  head();

  // Neck
  translate([0, 0, head_radius])
  neck();

  // Body
  translate([0, 0, head_radius + neck_length])
  body();

  // Tail
  translate([lizard_length / 3, 0, head_radius + neck_length + body_height / 2])
  rotate([0, 0, -20])
  tail();

  // Legs
  // Front Left
  translate([lizard_length / 6, lizard_width / 2, 0])
  rotate([0, 0, -45])
  leg();

  // Front Right
  translate([lizard_length / 6, -lizard_width / 2, 0])
  rotate([0, 0, 45])
  leg();

  // Back Left
  translate([2 * lizard_length / 3, lizard_width / 2, 0])
  rotate([0, 0, -45])
  leg();

  // Back Right
  translate([2 * lizard_length / 3, -lizard_width / 2, 0])
  rotate([0, 0, 45])
  leg();
}

// Render the lizard
agama();

