// Realistic Agama Lizard - Medium Complexity
// 3D printable model with anatomical features

// Global parameters
body_length = 60;
body_height = 12;
body_width = 18;
head_length = 15;
head_width = 12;
tail_segments = 15;
leg_thickness = 5;

module agama_body() {
    // Main torso - elongated ellipsoid
    difference() {
        scale([1.8, 1, 0.8])
            sphere(d=body_width, $fn=32);

        // Neck transition cut
        translate([body_length/2 - 5, 0, -body_height/2])
            cube([10, body_width, body_height], center=true);
    }
}

module agama_head() {
    // Triangular head shape
    union() {
        // Snout
        translate([body_length/2 + head_length/2, 0, 0])
            scale([1.5, 1, 0.7])
