module agouti_body() {
  // Create the body as a tapered cylinder
  difference() {
    cylinder(h = body_height, r1 = body_width / 2, r2 = body_width / 2, center = false);
    translate([0, 0, body_height])
      rotate_extrude(angle = body_taper_angle)
        translate([body_taper_start, 0, 0])
          square([body_length - body_taper_start, 1]);
  }
}

module agouti_head() {
  // Create the head as a rounded box
  union() {
    // Main head body
    translate([0, 0, 0])
      cube([head_length, head_width, head_height], center = true);

    // Nose
    translate([head_length / 2 + nose_length / 2, 0, 0])
      cylinder(h = nose_length, r = head_width / 2, center = false);

    // Ears
    translate([-head_length / 2 - ear_length * 2, 0, head_height/2])
      rotate([0, 0, 45])
        cylinder(h = ear_length, r = ear_width, center = false);

    translate([-head_length / 2 - ear_length * 2, 0, -head_height/2])
      rotate([0, 0, -45])
        cylinder(h = ear_length, r = ear_width, center = false);
  }
}

module agouti_legs() {
  // Create the legs as cylinders
  union() {
    // Front legs
    translate([body_length / 2 + 2, -body_width / 2 - leg_radius * 3, -leg_length])
      cylinder(h = leg_length, r = leg_radius, center = true);

    translate([body_length / 2 + 2, body_width / 2 + leg_radius * 3, -leg_length])
      cylinder(h = leg_length, r = leg_radius, center = true);

    // Back legs
    translate([body_length / 2 - 2, -body_width / 2 - leg_radius * 3, -leg_length])
      cylinder(h = leg_length, r = leg_radius, center = true);

    translate([body_length / 2 - 2, body_width / 2 + leg_radius * 3, -leg_length])
      cylinder(h = leg_length, r = leg_radius, center = true);
  }
}

module agouti_tail() {
