// Alligator Model in OpenSCAD

// --- Parameters ---
skin_color = [0.4, 0.2, 0.1]; // Dark greenish-brown
eye_color = [0.1, 0.3, 0.2]; // Green
scale_size = 2;
scale_height = 0.5;

// --- Functions ---

// Function to create a single scale
module scale(size, height) {
  difference() {
    cube([size, size, height]);
    translate([size/4, size/4, 0]) cube([size/2, size/2, height]); // Cut out the center
  }
}

// --- Body ---
module body(length, width, height) {
  difference() {
    cube([length, width, height]);

    // Cut out belly
    translate([0, 0, 0])
    cube([length, width * 0.5, height * 0.3]);
  }
}

// --- Head ---
module head(length, width, height) {
  difference() {
    cube([length, width, height]);

    // Create snout shape
    translate([length - length * 0.2, 0, 0])
    cube([length * 0.2, width, height]);
  }
}

// --- Tail ---
module tail(length, width, height) {
  difference() {
    cube([length, width, height]);
    translate([0,0,0]) cube([length * 0.7, width * 0.7, height * 0.7]);
  }
}


// --- Main Assembly ---
module alligator() {
  // Body
  color(skin_color)
  body(80, 20, 15);

  // Head
  color(skin_color)
  translate([60, 0, 5])
  head(30, 18, 12);

  // Eyes
  color(eye_color)
  translate([70, 5, 15])
  sphere(diameter=3);

  translate([70, -5, 15])
  sphere(diameter=3);

  // Tail
  color(skin_color)
  translate([0,0,-10])
  tail(40, 15, 10);

