// Medium complexity realistic style using basic primitives

// Parameters for printability
$fa = 2;
$fs = 0.5;
wall_thickness = 2;
scale_factor = 0.8;

module alligator() {
    // Main body
    color("darkolivegreen") {
        // Torso - main body segment
        union() {
            // Central body
            translate([-20, 0, 0]) scale([1.2, 0.8, 0.6]) sphere(d=40);

            // Tail segments
            for(i = [0:5]) {
                translate([-40 - i*8, 0, 0]) 
                rotate([0, -i*5, 0])
                scale([1 - i*0.1, 1 - i*0.15, 1 - i*0.2])
                sphere(d=30 - i*3);
            }

            // Head
            translate([15, 0, 5]) 
            scale([1.3, 1, 0.7]) 
            sphere(d=35);

            // Snout extension
            translate([30, 0, 3])
            scale([2, 0.6, 0.4])
            sphere(d=20);
        }

        // Legs
        module leg(x_pos, y_pos, angle) {
            translate([x_pos, y_pos, -15]) {
                rotate([0, 0, angle]) {
