        sphere(r=10, $fn=16);

        translate([-35, 0, 0])
        scale([0.1, 0.1, 0.05])
        sphere(r=5, $fn=8);
    }
}

module dorsal_fin() {
    // Main dorsal fin - triangular segments
    for(i=[-15:5:15]) {
        translate([i, 0, 4])
        rotate([0, 90, 0])
        cylinder(h=8, r1=2, r2=0.5, $fn=3);
    }
}

module pectoral_fin(left=true) {
    // Side fins - curved
    rotate([0, 0, left ? 30 : -30])
    translate([0, left ? 8 : -8, -2])
    rotate([0, 90, 0])
    scale([1, 0.5, 1])
    cylinder(h=10, r1=4, r2=1, $fn=16);
}

module pelvic_fin() {
    // Small fins underneath
    for(side=[-1, 1]) {
        translate([5, side*6, -3])
        rotate([90, 0, side*30])
        scale([0.5, 1, 0.3])
        cylinder(h=6, r1=2, r2=0.5, $fn=8);
    }
}

module caudal_fin() {
    // Tail fin - forked
