// Realistic Antlion Model - Medium Complexity
// Basic body length ~60mm

// Parameters
body_length = 60;
body_width = 25;
head_length = 15;
mandible_length = 18;
leg_thickness = 1.5;
leg_length = 35;
wing_length = 30;
abdomen_segments = 6;
$fn = 32;

// Main module
module antlion() {
    // Central body assembly
    union() {
        // Head with mandibles
        translate([body_length/2 + head_length/2, 0, 0])
            head();

        // Thorax
        translate([0, 0, 0])
            thorax();

        // Abdomen
        translate([-body_length/2 - 5, 0, 0])
            abdomen();

        // Legs - 3 pairs
        for(i = [0:2]) {
            rotate([0, 0, 120*i + 60])
                translate([body_length/3, 0, 0])
                    leg_pair(i);
        }

        // Wings folded over back
        wings();
    }
}

// Head module with compound eyes and mandibles
module head() {
    union() {
        // Main head capsule
        scale([1.2, 1, 0.8])
            sphere(d=head_length);

        // Compound eyes
        for(side = [-1, 1]) {
            translate([head_length/3, side*head_length/3, head_length/4])
                sphere(d=head_length/3);
            translate([head_length/3, side*head_length/3, -head_length/4])
                sphere(d=head_length/3);
        }

        // Mandibles
        for(side = [-1, 1]) {
            translate([head_length/2 + mandible_length/2, side*head_length/4, 0])
                rotate([0, 0, side * 20])
                    cylinder(h=mandible_length, d1=leg_thickness*2, d2=leg_thickness, center=false);
        }
    }
}

// Thorax module
module thorax() {
    union() {
        // Main thorax
        scale([1.5, 1.2, 1])
            sphere(d=body_width);

        // Thorax segments
        for(i = [0:2]) {
            translate([i*body_width/4 - body_width/2, 0, 0])
                scale([1.3 - i*0.1, 1.1 - i*0.05, 0.9 - i*0.05])
                    sphere(d=body_width);
        }
    }
}

// Segmented abdomen
module abdomen() {
    for(i = [0:abdomen_segments-1]) {
        translate([-i*body_width/2, 0, 0])
            scale([1 - i*0.08, 1 - i*0.1, 1 - i*0.05])
                sphere(d=body_width);
    }
}

// Leg pair module
module leg_pair(pair_num) {
    // Front, middle, and rear leg variations
    angle_offset = pair_num == 0 ? 30 : pair_num == 1 ? 0 : -30;

    for(side = [-1, 1]) {
        // Coxa
        translate([0, side*body_width/3, 0])
            rotate([0, 0, side * 30])
                cylinder(h=leg_thickness*3, d=leg_thickness*2);

        // Femur
        translate([0, side*body_width/2, 0])
            rotate([0, 0, side * (45 + angle_offset)])
                cylinder(h=leg_length/2, d1=leg_thickness*1.5, d2=leg_thickness);

        // Tibia
        translate([cos(side*(45+angle_offset))*leg_length/2, 
                  side*body_width/2 + sin(side*(45+angle_offset))*leg_length/2, 0])
            rotate([0, 0, side * (90 + angle_offset)])
                cylinder(h=leg_length/2, d=leg_thickness);
    }
}

// Folded wings
module wings() {
    for(side = [-1, 1]) {
        translate([0, side*body_width/2, body_width/2])
            rotate([0, 90, side * 15])
                scale([1, 0.3, 1])
                    cylinder(h=wing_length, d=body_width/2);
    }
}

// Generate the model
antlion();

