// Function to create a rounded rectangle
module rounded_rectangle(width, depth, radius) {
  hull() {
    translate([0, 0, 0]) cube([width, depth, 0], center = false);
    translate([width - radius, 0, 0]) sphere(r = radius, $fn = 20);
    translate([width, 0, 0]) cube([radius, depth, 0], center = false);
    translate([width, depth - radius, 0]) sphere(r = radius, $fn = 20);
    translate([width - radius, depth, 0]) cube([radius, radius, 0], center = false);
  }
}


// Main armadillo body - shell
module armadillo_shell() {
  // Main shell body
  difference() {
    rounded_rectangle(armadillo_length, armadillo_width, 10);
    translate([armadillo_length / 2, armadillo_width / 2, -shell_thickness])
      cube([armadillo_length, armadillo_width, shell_thickness*2], center=false);

    //Cut out armholes
    translate([armadillo_length/4, armadillo_width/2 - 10, 0]) cube([20, 20, armadillo_height], center = true);
    translate([3*armadillo_length/4, armadillo_width/2 - 10, 0]) cube([20, 20, armadillo_height], center = true);
  }

  // Top shell
  translate([0, 0, armadillo_height])
    cube([armadillo_length, armadillo_width, shell_thickness], center = false);
}

// Leg module
module armadillo_leg() {
  cylinder(r = leg_radius, h = leg_length, $fn = 20);
}

// Head module
module armadillo_head() {
    sphere(r = head_radius, $fn = 20);
}

// Tail module
