  // Legs
  translate([armadillo_length/4, -armadillo_width/2, -leg_length]) armadillo_leg();
  translate([3*armadillo_length/4, -armadillo_width/2, -leg_length]) armadillo_leg();
  translate([armadillo_length/4, armadillo_width/2, -leg_length]) armadillo_leg();
  translate([3*armadillo_length/4, armadillo_width/2, -leg_length]) armadillo_leg();

