        cylinder(r = radius, h = 1, center = true);
      }
      translate([width - (i + 1) * radius, depth, 0]) {
        cylinder(r = radius, h = 1, center = true);
      }
    }
    for (i = [0:1]) {
      translate([0, i * (depth - radius), 0]) {
        cylinder(r = radius, h = 1, center = true);
