        translate([x, 28, 8])
        union() {
            // Eye socket
            difference() {
                sphere(5);
                translate([0, 0, 2])
                    sphere(4.5);
            }
            // Eyeball
            translate([0, 0, 1])
                sphere(3);
        }
    }
}

module gills() {
    module gill_branch(x, angle) {
        rotate([0, 0, angle])
            translate([x, 32, 0])
                for(i = [0:2]) {
                    rotate([0, 0, 45 * i])
                        cylinder(h = 8, r1 = 1.5, r2 = 0.5);
                }
