    translate([0, 0, leg_height + body_height/2])
        scale([1.3, 0.8, 0.6])
        sphere(d = body_height);

    // Rump bulge
    translate([-body_length/2 + 5, 0, leg_height + body_height/3])
        scale([0.8, 0.9, 0.5])
        sphere(d = body_height * 0.8);

    // Belly
    translate([0, 0, leg_height - 2])
        cube([body_length * 0.6, body_height * 0.7, 4], center = true);
