    // Snout - tapered cylinder
    translate([body_length/2 + head_length/2, 0, leg_height + body_height * 0.7])
        rotate([0, -10, 0])
        cylinder(h = head_length, d1 = head_length * 0.6, d2 = head_length * 0.4);

    // Skull
    translate([body_length/2 + head_length/4, 0, leg_height + body_height * 0.9])
        sphere(d = head_length * 0.7);

    // Ears
    for(side = [-1, 1])
        translate([body_length/2, side * head_length/2, leg_height + body_height * 1.1])
            rotate([0, side * 20, 0])
            scale([0.3, 0.1, 0.8])
            sphere(d = head_length * 0.4);

