    for(side = [-1, 1])
        translate([body_length/2 + head_length * 0.7, side * head_length/4, leg_height + body_height * 0.9])
            rotate([0, -30 * side, 90])
            cylinder(h = tusk_length, d1 = 3, d2 = 2);
}

module legs() {
    // Four legs with slight taper
    for(x = [-body_length/3, body_length/3])
        for(y = [-body_height/2.5, body_height/2.5]) {
            translate([x, y, leg_height/2])
                cylinder(h = leg_height, d1 = 6, d2 = 5);

            // Hoof
            translate([x, y, 1])
                cylinder(h = 2, d = 7);
