module bat_ear(length, width, angle) {
    rotate([0, 0, angle])
    translate([0, 0, -1]) // Slightly offset to ensure proper layering
    difference() {
        hull() {
            translate([0, 0, 0]) cube([length, width, 1]);
            translate([length, 0, 0]) rotate([0, 90, 0]) cube([1, width, 1]);
        }

        // Small indentation for ear shape
        translate([length * 0.2, 0, 0])
            rotate([0, 0, -angle])
            cube([length * 0.8, width, 2], center = true);
    }
}

