// Bowerbird - Medium complexity realistic style
// Designed for 3D printing, no supports needed

// Overall dimensions: ~70mm length

module bowerbird() {
    // Body parameters
    body_length = 40;
    body_width = 25;
    body_height = 30;

    // Head parameters
    head_radius = 12;

    // Beak parameters
    beak_length = 15;
    beak_width = 3;
    beak_height = 5;

    // Tail parameters
    tail_length = 25;
    tail_width = 20;
    tail_height = 10;

    // Leg parameters
    leg_length = 18;
    leg_width = 3;
    foot_length = 8;
    foot_width = 6;

    // Wing parameters
    wing_length = 30;
    wing_width = 15;
    wing_thickness = 2;

    // Main body - elongated sphere
    translate([0, 0, body_height/2])
    scale([1.5, 1, 1])
    sphere(body_width/2);

    // Head
    translate([-body_length/2 - head_radius/2, 0, body_height/2 + head_radius/2])
    sphere(head_radius);

    // Beak
    translate([-body_length/2 - head_radius - beak_length/2, 0, body_height/2 + head_radius/2])
    rotate([0, 0, 0])
    scale([beak_length/beak_width, 1, beak_height/beak_width])
    cylinder(h=beak_width, r1=beak_width/2, r2=0);

    // Tail - fan shape
    for(i = [-30:10:30]) {
        rotate([0, i, 0])
        translate([-body_length/2 - tail_length/2, 0, body_height/2])
        scale([1, 1 - abs(i)/45, 1])
        cube([tail_length, tail_width/5, tail_height]);
    }

    // Left wing
    translate([body_length/4, body_width/2 + wing_width/2, body_height/2])
    rotate([0, 0, 30])
    scale([1, 0.8, 0.3])
    cube([wing_length, wing_width, wing_thickness]);

    // Right wing
    mirror([0, 1, 0])
    translate([body_length/4, body_width/2 + wing_width/2, body_height/2])
    rotate([0, 0, 30])
    scale([1, 0.8, 0.3])
    cube([wing_length, wing_width, wing_thickness]);

    // Legs and feet
    module leg(position) {
        translate(position)
        union() {
            // Upper leg
            translate([0, 0, leg_length/2])
            rotate([0, 20, 0])
            scale([1, 0.8, 2])
            sphere(leg_width/2);

            // Lower leg
            translate([0, 0, leg_length/4])
            cylinder(h=leg_length, r1=leg_width/2, r2=leg_width/3);

            // Foot
            translate([foot_length/2, 0, 0])
            scale([foot_length/leg_width, foot_width/leg_width, 1])
            sphere(leg_width/2);
        }
    }

    // Left leg
    leg([body_length/4, body_width/4, 0]);

    // Right leg
    leg([body_length/4, -body_width/4, 0]);

    // Eye details
    module eye(x, y, z) {
        translate([x, y, z])
        difference() {
            sphere(2.5);
            translate([0, 0, 1.5])
            sphere(1.5);
        }
    }

    eye(-body_length/2 - head_radius - 1, -head_radius/3, body_height/2 + head_radius/2);
    eye(-body_length/2 - head_radius - 1, head_radius/3, body_height/2 + head_radius/2);
}

// Display the bird standing upright
bowerbird();

