// Medium-complexity realistic blowfish for 3D printing
// Approx. 85 mm long, wall thickness 2 mm

module spike(d = 4, h = 6) {
    // tapered spike with 45° safe overhangs
    cylinder(h1 = h * 0.5, d1 = d, d2 = d * 0.4, $fn = 8);
    translate([0, 0, h * 0.5])
        cylinder(h2 = h * 0.5, d1 = d * 0.4, d2 = 0.1, $fn = 8);
}

module spikes_ring(r, count = 12) {
    for (i = [0 : count - 1]) {
        angle = i * 360 / count;
        translate([r * cos(angle), r * sin(angle), 0])
            rotate([90, 0, angle])
                spike();
    }
}

module body_shell() {
    difference() {
        // main puffed body
        sphere(d = 65, $fn = 60);
        // hollow inside
        sphere(d = 61, $fn = 60);
    }
}

module tail_fin() {
    difference() {
        // tail base
        translate([-30, 0, 0])
            rotate([0, 90, 0])
                scale([0.8, 1.2, 1])
                    cylinder(h = 20, d = 30, $fn = 30);
        // carve fins
        translate([-35, 0, 0])
            rotate([0, 90, 0])
                for (a = [-60 : 30 : 60])
                    rotate([0, a, 0])
                        cube([40, 2, 20], center = true);
    }
}

module eye() {
    difference() {
        // outer eye bulb
        sphere(d = 10, $fn = 20);
        // pupil
        color("black")
            translate([0, 0, 4])
                sphere(d = 5, $fn = 15);
    }
}

module mouth() {
    // puckered lips
    difference() {
        translate([31, 0, -5])
            rotate([90, 0, 0])
                cylinder(h = 8, d1 = 12, d2 = 8, $fn = 20);
        // mouth hole
        translate([35, 0, -5])
            rotate([90, 0, 0])
                cylinder(h = 10, d = 6, $fn = 15);
    }
}

// ---------- assembly ----------
union() {
    body_shell();

    // dorsal spikes
    for (z = [15 : 10 : 25])
        translate([0, 0, z])
            spikes_ring(25 + z * 0.5, count = 12);

    // lateral spikes
    for (a = [0 : 30 : 330])
        rotate([0, 0, a])
            translate([30, 0, 5])
                spikes_ring(15, count = 6);

    // tail
    translate([-32, 0, 0])
        tail_fin();

    // eyes
    translate([25, 15, 10])
        eye();
    translate([25, -15, 10])
        mirror([0, 1, 0])
            eye();

    // mouth
    mouth();
}

