// Booby Bird - Realistic Style, Medium Complexity

$fn = 32;  // Smooth curves

// Main dimensions
body_length = 45;
body_width = 25;
body_height = 30;

// Body module - elongated oval shape
module body() {
    difference() {
        // Main body
        scale([1, 0.8, 0.6])
        sphere(d=body_width);

        // Neck opening
        translate([0, body_width/2 - 5, body_height/4])
        rotate([90, 0, 0])
        cylinder(h=body_width, d1=12, d2=8, center=true);

        // Tail opening
        translate([-body_length/2 + 5, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=15, d1=10, d2=8, center=true);
    }
}

// Head module
module head() {
    translate([0, body_width/2 - 3, body_height/3 + 5])
    union() {
        // Main head shape
        scale([0.9, 1, 1.1])
        sphere(d=15);

        // Beak
        translate([0, 8, -2])
        rotate([30, 0, 0])
        scale([0.3, 0.4, 1])
        cylinder(h=10, d1=8, d2=0);

        // Eyes
        for(x = [-4, 4]) {
            translate([x, 5, 2])
            color("black")
            sphere(d=3);
        }
    }
}

// Wing module
module wing(is_left = true) {
    side = is_left ? 1 : -1;
    translate([side * body_width/4, 0, body_height/3])
    rotate([0, 0, is_left ? 30 : -30])
    scale([0.8, 2, 0.2])
    sphere(d=body_width);
}

// Tail module
module tail() {
    translate([-body_length/2 + 3, 0, 0])
    rotate([0, -20, 0])
    scale([0.5, 2, 0.3])
    sphere(d=15);
}

// Feet module
module feet() {
    for(side = [-1, 1]) {
        translate([side * 5, body_width/4, -body_height/2 + 3])
        union() {
            // Leg
            cylinder(h=8, d=3);

            // Webbed foot
            translate([0, 0, -4])
            rotate([0, 0, 45])
            scale([2, 0.3, 0.2])
            sphere(d=8);

            // Toes
            for(angle = [-30, 0, 30]) {
                translate([0, 0, -6])
                rotate([0, 0, angle])
                translate([6, 0, 0])
                scale([0.5, 0.3, 0.3])
                sphere(d=6);
            }
        }
    }
}

// Assembly
color("LightBlue")
union() {
    // Main body
    body();

    // Head and neck connection
    translate([0, body_width/2 - 5, body_height/4])
    rotate([90, 0, 0])
    cylinder(h=10, d1=8, d2=12);

    head();

    // Wings
    wing(true);
    wing(false);

    // Tail
    tail();

    // Feet
    feet();
}

