        translate([-18,0,12])
            rotate([0,10,0])
            scale([2,0.4,0.3])
            cylinder(h=15, r1=6, r2=3);
    }
}

// Head with beak
module head() {
    translate([12,0,22]) {
        union() {
            // Head
            scale([0.9,0.8,0.9])
            sphere(8);

            // Beak
            translate([6,0,0])
                rotate([0,-20,0])
                scale([1.5,0.3,0.3])
                cylinder(h=8, r1=3, r2=0.5);

            // Eye
            translate([4,4,2])
                sphere(1.5);
            translate([4,-4,2])
                sphere(1.5);

            // Crest
            translate([0,0,6])
                scale([0.3,0.2,1])
                cylinder(h=5, r=3);
        }
    }
}

// Wing
module wing(side=1) {
    translate([0,side*8,18]) {
        rotate([0,0,side*-10]) {
            difference() {
                // Main wing
                scale([1.2,0.4,0.1])
                sphere(15);

                // Wing curve
                translate([5,side*-8,0])
                    rotate([0,0,20])
                    scale([0.8,0.3,0.2])
                    cube([20,15,5]);
            }
        }
    }
}

// Leg and foot
module leg(side=1) {
    translate([2,side*5,5]) {
        union() {
            // Upper leg
            rotate([80,0,0])
                cylinder(h=8, r=1.5);

            // Lower leg
            translate([0,0,-8])
                rotate([10,0,0])
                cylinder(h=8, r=1.2);

            // Foot with toes
            translate([0,0,-15.5]) {
                // Main foot
                cylinder(h=1, r=2);

                // Toes
