// Simple 3D bunting made of triangular flags on a cord
// Parameters
flag_w = 30;        // width of each flag
flag_h = 20;        // height from cord to tip
flag_t = 1;         // thickness of each flag
spacing = 5;        // gap between flags
cord_d = 2;         // cord diameter
num_flags = 8;      // how many flags

module flag() {
    // Simple triangular flag
    linear_extrude(height = flag_t)
        polygon([[0, 0], [flag_w/2, -flag_h], [flag_w, 0]]);
}

// Cord
translate([0, 0, flag_t/2 + cord_d/2])
    rotate([90, 0, 0])
        cylinder(h = num_flags*(flag_w+spacing), d = cord_d, center = false);

// Flags
for (i = [0 : num_flags-1]) {
    translate([i*(flag_w+spacing), 0, 0])
        flag();
}

