// Realistic Bunting - Medium Complexity
// 3D printable bunting with realistic fabric folds and string

// Parameters
flag_width = 30;
flag_height = 25;
flag_thickness = 2;
string_diameter = 2;
string_length = 90;
num_flags = 5;
fold_depth = 3;

// Overall scale
scale_factor = 1.5;

// Flag module with realistic folds
module flag_with_folds() {
    // Base flag triangle
    difference() {
        union() {
            // Main flag body
            linear_extrude(height=flag_thickness)
                polygon(points=[[0,0], [flag_width,0], [flag_width/2,flag_height]]);

            // Top fold detail
            translate([flag_width/2, flag_height*0.7, flag_thickness])
                rotate([90,0,0])
                    cylinder(h=fold_depth, r1=flag_width/4, r2=flag_width/5, $fn=20);

            // Side fold
            translate([flag_width*0.8, flag_height*0.4, 0])
                rotate([0,0,-30])
                    cube([flag_width/3, flag_height/4, flag_thickness*2]);
        }

        // Hanging loop hole
        translate([flag_width/2, 0, -1])
            cylinder(h=flag_thickness+2, r=string_diameter/2+1, $fn=20);
    }
}

// String module
module string() {
    // Main string
    translate([0, -string_diameter/2, 0])
        rotate([0,90,0])
            cylinder(h=string_length, r=string_diameter/2, $fn=20);

    // Add subtle curve to string
    for(i=[0:5]) {
        translate([i*string_length/6, -sin(i*30)*2-string_diameter/2, 0])
            rotate([0,90,0])
                cylinder(h=string_length/10, r=string_diameter/2, $fn=20);
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    // Create string first
    string();

    // Add flags along string with realistic spacing and slight rotation
    for(i=[0:num_flags-1]) {
        translate([(i+0.5)*string_length/num_flags, -string_diameter, 0])
            rotate([0,0,i*5-10])  // Slight flutter effect
                flag_with_folds();
    }

    // Add connecting knots between flags
    for(i=[0:num_flags-1]) {
        translate([(i+0.5)*string_length/num_flags, -string_diameter/2, 0])
            sphere(r=string_diameter*0.8, $fn=20);
    }

    // End details
    translate([0, -string_diameter/2, 0])
        sphere(r=string_diameter, $fn=20);
    translate([string_length, -string_diameter/2, 0])
        sphere(r=string_diameter, $fn=20);
}

