// Binturong (Bearcat) 3D Printable Model
// Medium complexity realistic style

$fn = 32;
body_length = 60;
body_height = 35;
leg_height = 15;
tail_length = 50;
head_size = 20;

module ear() {
    difference() {
        scale([1, 0.8, 1]) sphere(4);
        translate([0, 0, -1]) scale([0.7, 0.7, 1]) sphere(3.5);
    }
}

module leg(is_front = true) {
    union() {
        // Upper leg
        translate([0, 0, leg_height/2]) 
            scale([1.2, 1.2, 1]) 
            cylinder(h=leg_height, r1=4, r2=3);

        // Lower leg
        translate([0, 0, -leg_height/2]) 
            cylinder(h=leg_height*0.8, r1=3, r2=2.5);

        // Paw
        translate([0, 0, -leg_height-2]) 
            sphere(3);
    }
}

module tail() {
    hull() {
        translate([0, 0, 0]) sphere(6);
        translate([0, -tail_length, 5]) sphere(4);
        translate([0, -tail_length*1.2, 8]) sphere(3);
    }
}

module whiskers() {
    for(i = [-3:3]) {
        rotate([0, 0, i*15]) 
            translate([8, 0, 0]) 
            cylinder(h=8, r=0.5);
    }
}

// Main body
union() {
    // Torso
    translate([0, 0, body_height/2]) 
        scale([1.2, 1.5, 0.8]) 
        sphere(body_length/3);

    // Head
    translate([0, body_length/3, body_height/2+5]) {
        scale([1.1, 1.2, 1]) sphere(head_size/2);

        // Snout
        translate([0, head_size/2, -2]) 
            scale([0.8, 0.6, 0.6]) sphere(6);

        // Ears
        translate([-head_size/2, head_size/3, head_size/2]) ear();
        translate([head_size/2, head_size/3, head_size/2]) mirror([1,0,0]) ear();

        // Eyes
        translate([-head_size/3, head_size/2-2, head_size/4]) 
            color("black") sphere(1.5);
        translate([head_size/3, head_size/2-2, head_size/4]) 
            color("black") sphere(1.5);

        // Nose
        translate([0, head_size/2+3, -1]) 
            color("black") sphere(2);

        // Whiskers
        translate([0, head_size/2+2, 0]) whiskers();
    }

    // Front legs
    translate([-body_length/4, body_length/4, -body_height/4]) leg(true);
    translate([body_length/4, body_length/4, -body_height/4]) leg(true);

    // Rear legs
    translate([-body_length/4, -body_length/3, -body_height/4]) leg(false);
    translate([body_length/4, -body_length/3, -body_height/4]) leg(false);

    // Tail
    translate([0, -body_length/2, 0]) 
        rotate([0, 20, 0]) 
        tail();

    // Body fur texture (subtle)
    for(i = [0:3]) {
        rotate([0, 0, i*90]) 
            translate([0, 0, body_height/2]) 
            difference() {
                sphere(body_length/3+0.5);
                sphere(body_length/3);
            }
    }
}

