// Caiman OpenSCAD model - medium complexity realistic style
// Overall dimensions approximately 90mm long, 25mm wide, 15mm high

$fn=32;

// Main body module
module caiman_body() {
    // Main torso - elongated ellipsoid
    scale([1.2, 0.7, 0.4])
    sphere(r=20);

    // Tail - tapered cylinder segments
    for(i=[0:3]) {
        translate([25+i*10, 0, -i*2])
        rotate([0, -10-i*5, 0])
        scale([1-i*0.15, 1-i*0.15, 1-i*0.1])
        cylinder(h=12, r1=8-i*2, r2=6-i*2);
    }
}

// Head module
module caiman_head() {
    // Main head shape
    translate([-25, 0, 2])
    scale([1.5, 0.8, 0.7])
    sphere(r=12);

    // Snout extension
    translate([-40, 0, 0])
    scale([1.8, 0.5, 0.4])
    sphere(r=8);

    // Eye sockets
    translate([-30, 6, 6])
    sphere(r=3);
    translate([-30, -6, 6])
    sphere(r=3);

    // Eyes
    color("black")
    translate([-31, 6.5, 6.5])
    sphere(r=1.5);
    translate([-31, -6.5, 6.5])
    sphere(r=1.5);
}

// Leg module - creates one leg, use mirror for others
module caiman_leg(side, front_back) {
    // Upper leg
    translate([front_back*10, side*8, -5])
    rotate([0, 0, side*30])
    scale([0.8, 0.8, 2])
    cylinder(h=8, r=3);

    // Lower leg
    translate([front_back*10, side*12, -13])
    rotate([0, 0, side*20])
    scale([0.7, 0.7, 1.5])
    cylinder(h=10, r=2.5);

    // Foot
    translate([front_back*10, side*15, -20])
    rotate([0, 0, side*-10])
    scale([2, 0.8, 0.5])
    cube([8, 4, 2]);

    // Toes
    for(toe=[-1:1]) {
        translate([front_back*12+toe*2, side*17, -20])
        scale([0.5, 0.2, 0.3])
        cube([4, 2, 1]);
    }
}

// Scales texture along back
module back_scales() {
    for(i=[-20:5:30]) {
        translate([i, 0, 5])
        scale([1, 0.3, 0.3])
        sphere(r=2);
    }
}

// Jaw line detail
module jaw_detail() {
    // Upper jaw ridge
    for(i=[-45:3:-25]) {
        translate([i, 0, 3])
        scale([0.5, 0.3, 0.5])
        sphere(r=1.5);
    }
}

// Main assembly
union() {
    // Body and tail
    caiman_body();

    // Head
    caiman_head();

    // Legs - front and back pairs
    // Front legs
    caiman_leg(1, -1);  // Right front
    caiman_leg(-1, -1); // Left front

    // Back legs
    caiman_leg(1, 1);   // Right back
    caiman_leg(-1, 1);  // Left back

    // Back scales
    back_scales();

    // Jaw detail
    jaw_detail();

    // Nostrils
    translate([-42, 2, 2])
    sphere(r=1);
    translate([-42, -2, 2])
    sphere(r=1);
}

