// Caribou - Realistic Medium Complexity
// 3D printable model ~80mm length

// Global parameters
body_len = 60;
body_wid = 25;
body_hgt = 30;
leg_len = 25;
head_len = 20;
head_wid = 12;
head_hgt = 14;

module body() {
    // Main torso
    translate([0, 0, 12])
    scale([1, 0.8, 0.9])
    sphere(d=body_wid, $fn=20);

    // Rump
    translate([-body_len/2-5, 0, 8])
    scale([1.2, 1, 0.8])
    sphere(d=body_wid+5, $fn=20);

    // Chest
    translate([body_len/2, 0, 10])
    scale([1, 1.1, 1.2])
    sphere(d=body_wid, $fn=20);
}

module leg(pos, angle) {
    translate(pos)
    rotate([0, angle, 0]) {
        // Upper leg
        translate([0, 0, 12])
        cylinder(h=leg_len-5, d1=6, d2=4, $fn=8);

        // Lower leg
        translate([0, 0, 12+leg_len-5])
        cylinder(h=leg_len-5, d1=4, d2=3, $fn=8);

        // Hoof
        translate([0, 0, 12+2*leg_len-10])
        cube([4, 8, 3], center=true);
    }
}

module antler() {
    translate([0, 0, 0])
    rotate([0, -20, 0]) {
        // Main beam
        cylinder(h=20, d1=3, d2=2, $fn=6);

        // Tines
        for(i=[0:30:120]) {
            rotate([0, 0, i])
            translate([0, 0, 5+i/6])
            rotate([0, 30, 0])
            cylinder(h=10-i/10, d1=2, d2=1, $fn=5);

            rotate([0, 0, -i])
            translate([0, 0, 5+i/6])
            rotate([0, 30, 0])
            cylinder(h=8-i/12, d1=2, d2=1, $fn=5);
        }
    }
}

module head() {
    // Skull
    translate([body_len/2+head_len/2, 0, 25])
    rotate([0, -20, 0])
    scale([1.3, 1, 1])
    sphere(d=head_wid, $fn=20);

    // Muzzle
    translate([body_len/2+head_len, 0, 22])
    scale([2, 0.8, 0.6])
    sphere(d=head_wid/2, $fn=20);

    // Ears
    translate([body_len/2+head_len/2-3, 6, 32])
    rotate([0, -30, 0])
    scale([0.5, 1, 2])
    sphere(d=4, $fn=8);

    translate([body_len/2+head_len/2-3, -6, 32])
    rotate([0, -30, 0])
    scale([0.5, 1, 2])
    sphere(d=4, $fn=8);

    // Antlers
    translate([body_len/2+head_len/2-5, 8, 38])
    antler();

    translate([body_len/2+head_len/2-5, -8, 38])
    mirror([0, 1, 0])
    antler();
}

module tail() {
    translate([-body_len/2-7, 0, 18])
    rotate([0, 40, 0])
    scale([1, 0.5, 0.3])
    sphere(d=8, $fn=10);
}

// Assembly
