module carp() {
    // Body - main oval shape
    difference() {
        union() {
            // Main body ellipsoid with flattened sides
            scale([1.3, 0.6, 0.7])
                sphere(d=40, $fn=64);

            // Tail extension
            translate([-20, 0, 0])
                scale([0.8, 0.3, 0.4])
                    sphere(d=30, $fn=32);
        }

        // Cut bottom flat for printing
        translate([0, 0, -15])
            cube([100, 100, 30], center=true);
    }

    // Head section - slightly wider
    translate([15, 0, 2])
        scale([1.1, 1.2, 0.9])
            sphere(d=20, $fn=32);

    // Mouth - small protrusion
    translate([22, 0, -2])
        rotate([0, 20, 0])
            scale([1, 0.8, 0.6])
                cylinder(h=8, d1=8, d2=6, $fn=16);

    // Eyes
    for(side = [-1, 1]) {
        translate([18, side*6, 3])
            sphere(d=4, $fn=16);
        translate([19, side*6.5, 3.5])
            sphere(d=2, $fn=8);
    }

    // Dorsal fin
    module dorsal_fin() {
        for(i = [0:3]) {
            translate([-5 - i*6, 0, 6 + sin(i*45)*2])
                rotate([0, 90 - i*10, 0])
                    scale([0.3, 1, 0.1])
                        cylinder(h=8, d1=10, d2=2, $fn=3);
        }
    }
    dorsal_fin();

    // Pectoral fins
    module pectoral_fin(side) {
        translate([8, side*10, -2])
            rotate([0, 0, side*30])
                scale([1, 0.3, 0.1])
                    cylinder(h=12, d1=8, d2=4, $fn=3);
    }
    pectoral_fin(1);
    pectoral_fin(-1);

    // Pelvic fins
    module pelvic_fin(side) {
        translate([0, side*8, -3])
            rotate([0, 0, side*45])
                scale([0.8, 0.3, 0.1])
                    cylinder(h=10, d1=6, d2=3, $fn=3);
    }
    pelvic_fin(1);
    pelvic_fin(-1);

    // Anal fin
    module anal_fin() {
        for(i = [0:2]) {
            translate([-12 - i*4, 0, -4])
                rotate([0, 90 + i*15, 0])
                    scale([0.3, 0.8, 0.1])
                        cylinder(h=6, d1=6, d2=2, $fn=3);
        }
    }
    anal_fin();

    // Tail fin
    translate([-30, 0, 0])
        rotate([0, 90, 0])
            scale([0.2, 1.5, 1])
                cylinder(h=15, d1=20, d2=0, $fn=3);

    // Scales texture - simple pattern
    module scales() {
        for(x = [-20:5:15]) {
            for(y = [-8:4:8]) {
                for(z = [-5:3:5]) {
                    if(norm([x,y,z]) < 20) {
                        translate([x, y, z + sin(x*0.5)*2])
