// Realistic Cat - Medium Complexity
// Designed for 3D printing with no overhangs

// Overall dimensions: ~70mm long, ~40mm high, ~35mm wide

module cat_body() {
    // Main torso - oval shape
    union() {
        // Torso base
        scale([1.2, 1, 0.8])
            sphere(d=30, $fn=32);

        // Chest extension
        translate([-10, 0, -5])
            scale([0.8, 1, 0.6])
            sphere(d=25, $fn=32);
    }
}

module cat_head() {
    // Head with ears and muzzle
    translate([20, 0, 12]) {
        rotate([0, -10, 0]) {
            // Main head sphere
            sphere(d=20, $fn=32);

            // Muzzle
            translate([8, 0, -2])
                scale([1.2, 0.8, 0.6])
                sphere(d=12, $fn=32);

            // Left ear
            translate([0, -8, 8])
                rotate([30, 0, -20])
                scale([0.3, 0.1, 0.8])
                cylinder(d1=12, d2=6, h=8, $fn=3);

            // Right ear
            translate([0, 8, 8])
                rotate([30, 0, 20])
                scale([0.3, 0.1, 0.8])
                cylinder(d1=12, d2=6, h=8, $fn=3);

            // Eyes (indents)
            translate([10, -3.5, 2])
                sphere(d=3, $fn=16);
            translate([10, 3.5, 2])
                sphere(d=3, $fn=16);

            // Nose
            translate([13, 0, -1])
                scale([0.8, 0.6, 0.4])
                sphere(d=4, $fn=16);
        }
    }
}

module cat_legs() {
    // Front legs
    for(side = [-1, 1]) {
        translate([10, side*8, -5]) {
            // Upper leg
            rotate([0, side*10, 0])
                cylinder(d1=6, d2=5, h=15, $fn=16);

            // Paw
            translate([0, 0, -15])
                sphere(d=7, $fn=16);
        }
    }

    // Back legs
    for(side = [-1, 1]) {
        translate([-15, side*8, -5]) {
            // Thigh
            rotate([0, -side*20, 0])
                scale([1.2, 1, 1])
                cylinder(d1=7, d2=6, h=12, $fn=16);

            // Lower leg
            translate([-3, 0, -12])
                rotate([30, 0, 0])
                cylinder(d1=5, d2=4, h=10, $fn=16);

            // Paw
            translate([-5, 0, -22])
                sphere(d=7, $fn=16);
        }
    }
}

module cat_tail() {
    // Tail in relaxed curved position
    translate([-18, 0, 8]) {
        rotate([0, 0, -45]) {
            for(i = [0:5]) {
                translate([sin(i*15)*3, -cos(i*15)*3, i*3])
                    rotate([0, 0, i*15])
                    sphere(d=6-i*0.5, $fn=16);
            }
        }
    }
}

module cat_whiskers() {
