    for(side = [-1, 1]) {
        for(angle = [-30, 0, 30]) {
            translate([20, side*3, 10]) {
                rotate([0, 0, side*angle])
                translate([5, 0, 0])
                    cylinder(d=0.5, h=8, $fn=8);
            }
        }
    }
}

