// Realistic 3D Printable Caterpillar
// Medium complexity with anatomical features

$fn = 36;

// Main parameters
body_length = 80;
segment_count = 10;
segment_radius = 8;
head_radius = 9;
leg_length = 6;

// Create the complete caterpillar
module caterpillar() {
    // Body segments
    for (i = [0:segment_count-1]) {
        translate([i * (body_length / segment_count), 0, 0]) 
            body_segment(i);
    }

    // Head
    translate([(segment_count) * (body_length / segment_count), 0, 0]) 
        head();

    // Legs - three pairs per segment
    for (i = [0:segment_count-1]) {
        segment_pos = i * (body_length / segment_count);

        // Left legs
        translate([segment_pos, segment_radius * 0.8, -segment_radius * 0.3])
            rotate([0, 90, -30]) leg();
        translate([segment_pos, segment_radius * 0.8, 0])
            rotate([0, 90, -45]) leg();
        translate([segment_pos, segment_radius * 0.8, segment_radius * 0.3])
            rotate([0, 90, -60]) leg();

        // Right legs
        translate([segment_pos, -segment_radius * 0.8, -segment_radius * 0.3])
            rotate([0, 90, 30]) leg();
        translate([segment_pos, -segment_radius * 0.8, 0])
            rotate([0, 90, 45]) leg();
        translate([segment_pos, -segment_radius * 0.8, segment_radius * 0.3])
            rotate([0, 90, 60]) leg();
    }
}

// Individual body segment with realistic features
module body_segment(index) {
    segment_width = segment_radius * 2;

    // Main segment body
    difference() {
        // Elliptical segment
        scale([1.2, 1, 0.8]) 
            sphere(r = segment_radius);

        // Bottom flat for printing
        translate([0, 0, -segment_radius]) 
            cube([segment_width * 2, segment_width * 2, segment_radius], center = true);
    }

    // Segment ridges for realism
    for (j = [0:3]) {
        rotate([0, 0, j * 90]) 
            translate([segment_radius * 0.3, 0, segment_radius * 0.5])
                scale([0.3, 0.1, 0.3]) 
                    sphere(r = segment_radius * 0.8);
    }

    // Spiracles (breathing holes)
    translate([0, segment_radius * 0.7, segment_radius * 0.2])
        sphere(r = 0.5);
    translate([0, -segment_radius * 0.7, segment_radius * 0.2])
        sphere(r = 0.5);
}

// Realistic head with mandibles and eyes
module head() {
    // Main head sphere
    sphere(r = head_radius);

    // Eyes - compound eye effect
    for (eye in [[head_radius * 0.7, head_radius * 0.5, head_radius * 0.6],
                 [head_radius * 0.7, -head_radius * 0.5, head_radius * 0.6]]) {
        translate(eye) {
            difference() {
                sphere(r = 1.5);
                // Pupil
                translate([0.5, 0, 0])
                    sphere(r = 0.8);
            }
        }
    }

    // Mandibles
    translate([head_radius * 0.8, head_radius * 0.4, -head_radius * 0.5])
        rotate([0, -20, 0])
            scale([1, 0.4, 0.6])
                cylinder(r1 = 1.5, r2 = 0.5, h = 3);
    translate([head_radius * 0.8, -head_radius * 0.4, -head_radius * 0.5])
        rotate([0, -20, 0])
            scale([1, 0.4, 0.6])
                cylinder(r1 = 1.5, r2 = 0.5, h = 3);

    // Antennae
    translate([head_radius * 0.5, head_radius * 0.3, head_radius * 0.8])
        rotate([0, -30, 30])
            cylinder(r = 0.3, h = 4);
    translate([head_radius * 0.5, -head_radius * 0.3, head_radius * 0.8])
        rotate([0, -30, -30])
            cylinder(r = 0.3, h = 4);
}

// Realistic leg with segments
module leg() {
    union() {
        // Main leg segment
        cylinder(r1 = 1, r2 = 0.6, h = leg_length);

        // Foot/claw
        translate([0, 0, leg_length])
            sphere(r = 0.7);
    }
}

