    }

    // Tail - tapered cylinder
    translate([-17, 0, 12]) {
        rotate([0, 20, 0]) {
            cylinder(h=20, r1=6, r2=3);
        }
    }
}

module head() {
    // Head sphere
    translate([12, 0, 20]) {
        sphere(7);
    }

    // Beak - cone
    translate([19, 0, 20]) {
        rotate([0, -10, 0]) {
            cylinder(h=8, r1=1.5, r2=0);
        }
    }

    // Eye
    translate([14, 3.5, 21]) {
        sphere(1.2);
    }
    translate([14, -3.5, 21]) {
        sphere(1.2);
    }
}

module wing() {
    // Main wing - elliptical shape
    translate([0, 9, 18]) {
        rotate([0, 0, -20]) {
            scale([1.2, 0.3, 1]) {
                sphere(7);
            }
        }
    }

    // Wing tip
    translate([5, 12, 17]) {
        rotate([0, -30, -30]) {
            scale([0.8, 0.2, 0.6]) {
                sphere(4);
            }
        }
    }
}

module legs() {
    // Left leg
    translate([5, 2, 0]) {
        cylinder(h=12, r=1);
        translate([0, 0, 12]) {
            rotate([0, 90, 0]) {
                cylinder(h=4, r=0.8);
            }
        }
    }

    // Right leg
    translate([5, -2, 0]) {
        cylinder(h=12, r=1);
        translate([0, 0, 12]) {
            rotate([0, 90, 0]) {
                cylinder(h=4, r=0.8);
            }
        }
    }
}

module feathers() {
    // Tail feathers layers
    for(i = [0:2]) {
        translate([-16 - i*2, 0, 10 + i*2]) {
            rotate([0, 25 - i*5, 0]) {
                scale([1, 0.1, 0.3]) {
                    cylinder(h=15 - i*2, r1=5 - i, r2=2 - i*0.5);
