        translate([0, 8, 8]) rotate([0, 30, 0]) 
            scale([0.6, 0.3, 1.5]) sphere(d=8);
        translate([0, -8, 8]) rotate([0, -30, 0]) 
            scale([0.6, 0.3, 1.5]) sphere(d=8);

        // Nose
        translate([11, 0, -2]) sphere(d=3);

        // Whiskers
        for(i=[-1,1]) {
            translate([8, i*5, -3]) rotate([0, 0, i*30]) 
                cylinder(h=12, d=0.8);
            translate([8, i*5, -3]) rotate([0, 0, i*15]) 
                cylinder(h=10, d=0.8);
        }
    }

    // Tail - bushy
    translate([20, 0, 5]) rotate([0, 0, 20]) {
        for(i=[0:30:150]) {
            rotate([0, 0, i]) 
                translate([0, 0, -i/5]) 
                scale([0.8, 1, 0.8]) 
                sphere(d=12 - i/10);
        }
    }

    // Front legs
    for(side=[-1,1]) {
        translate([-15, side*10, -12]) {
            // Upper leg
            rotate([0, -side*20, 0]) cylinder(h=12, d=6);
            // Paw
            translate([0, 0, -7]) sphere(d=7);
            // Toes
            for(j=[-1,0,1]) {
                translate([j*2, 0, -12]) sphere(d=3);
            }
        }
