    // Hind legs
    for(side=[-1,1]) {
        translate([15, side*12, -12]) {
            // Thigh
            rotate([0, side*30, 0]) cylinder(h=15, d=8);
            // Lower leg
            translate([0, 0, -9]) rotate([0, side*20, 0]) 
                cylinder(h=10, d=6);
