// Chimpanzee – medium-complexity realistic style
// Overall height ≈ 75 mm, printable in one piece (no severe overhangs)

// Wall thickness / printability factor
th = 1.2;

module roundedBox(s, r) {
    // simple rounded box for body parts
    hull() {
        for (dx = [0, s.x-2*r], dy = [0, s.y-2*r], dz = [0, s.z-2*r])
            translate([dx+r, dy+r, dz+r]) sphere(r);
    }
}

module head() {
    // cranium
    translate([0,0,62]) rotate([0,-10,0]) union() {
        color("BurlyWood") difference() {
            scale([1, 0.9, 1]) sphere(10);
            // eye sockets
            translate([4, 5, 3]) sphere(2.5);
            translate([-4, 5, 3]) sphere(2.5);
            // mouth cavity
            translate([0, 6, -2]) scale([1.2,1,0.8]) sphere(3);
        }
        // muzzle
        translate([0, 7, -2]) scale([1.2, 1.6, 1]) sphere(6);
        // ears
        translate([9, 0, 3]) rotate([0,0,20]) scale([1,2.5,0.7]) sphere(3);
        translate([-9, 0, 3]) rotate([0,0,-20]) scale([1,2.5,0.7]) sphere(3);
    }
}

module torso() {
    // rib-cage like rounded box
    translate([0,0,30]) rotate([0,0,0]) color("SaddleBrown")
        roundedBox([22, 18, 38], 6);
}

module arm(side) { // side = 1 right, -1 left
    // shoulder
    translate([side*11,0,50]) rotate([0,0,side*30]) union() {
        color("SaddleBrown") sphere(5);
        // upper arm
        translate([0,0,-12]) rotate([30,0,0])
            cylinder(h=18, r1=4, r2=3.5, $fn=16);
        // lower arm
        translate([0,0,-12-18]) rotate([20,0,0])
            cylinder(h=16, r1=3.5, r2=3, $fn=16);
        // hand
        translate([0,0,-12-18-16]) rotate([10,0,0]) union() {
            scale([1.2,0.8,0.6]) sphere(4);
            // simplified fingers as small cylinders
            for(i=[-1:1])
                translate([side*2*i,0,-2]) rotate([0,side*20,0])
                    cylinder(h=5, r=1.2, $fn=8);
        }
    }
}

module leg(side) { // side = 1 right, -1 left
    translate([side*7,0,8]) rotate([0,0,side*10]) union() {
        // thigh
        color("SaddleBrown") cylinder(h=20, r1=5, r2=4.5, $fn=16);
        // shin
        translate([0,0,-20]) rotate([15,0,0])
            cylinder(h=18, r1=4.5, r2=3.5, $fn=16);
        // foot
        translate([0,0,-20-18]) rotate([0,0,0]) union() {
            scale([1.3,2,0.7]) sphere(4);
            for(i=[-1:1])
                translate([side*i*2,3,0]) rotate([0,20,0])
                    cylinder(h=4, r=1.2, $fn=8);
        }
    }
}

// assembly
union() {
    head();
    torso();
    arm(1);
    arm(-1);
    leg(1);
    leg(-1);
}

