// Cicada - Realistic style, medium complexity
// 3D printable model using only basic primitives

// Body dimensions
body_length = 50;
body_width = 20;
body_height = 15;

// Head dimensions
head_length = 12;
head_width = 15;
head_height = 10;

// Wing dimensions
wing_length = 40;
wing_width = 15;
wing_thickness = 1;

// Leg dimensions
leg_thickness = 2;
leg_length = 12;

// Overall scaling
scale_factor = 1.5;

// Main body module
module cicada_body() {
    // Thorax
    translate([0, 0, 0])
    scale([1, 0.8, 0.7])
    sphere(d=body_height);

    // Abdomen
    translate([-body_length/2, 0, -2])
    scale([1.8, 1, 0.8])
    sphere(d=body_height);

    // Segments
    for (i = [1:5]) {
        translate([-i*5, 0, -3 - i*0.5])
        scale([1.5 - i*0.1, 1 - i*0.05, 0.7 - i*0.05])
        sphere(d=body_height - 2);
    }
}

// Head module
module cicada_head() {
    // Main head
    translate([head_length + 8, 0, 2])
    scale([1.2, 1, 0.9])
    sphere(d=head_height);

    // Eyes
    translate([head_length + 12, head_width/2, 2])
    sphere(d=6);

    translate([head_length + 12, -head_width/2, 2])
    sphere(d=6);

    // Proboscis
    translate([head_length + 15, 0, -2])
    rotate([90, 0, 0])
    cylinder(h=8, d1=3, d2=1);
}

// Wing module
module cicada_wing() {
    difference() {
        // Main wing shape
        translate([5, 12, 5])
        rotate([0, -20, 10])
        scale([1, 0.3, 0.05])
        sphere(d=wing_length);

        // Wing veins
        for (i = [-2:2]) {
            translate([i*5 + 5, 12 + i*2, 5.5])
            rotate([0, -20, 10])
            scale([0.8, 0.02, 0.05])
            sphere(d=wing_length - 10);
        }
    }
}

// Leg module
module cicada_leg() {
    // Upper leg
    rotate([15, 0, 0])
    cylinder(h=leg_length, d=leg_thickness);

    // Lower leg
    translate([0, 0, leg_length])
    rotate([-30, 0, 0])
    cylinder(h=leg_length*0.8, d=leg_thickness*0.8);

    // Foot
    translate([0, 0, leg_length + leg_length*0.8])
    rotate([-90, 0, 0])
    cylinder(h=3, d=leg_thickness*1.2);
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        // Body
        cicada_body();

        // Head
        cicada_head();

        // Wings
        cicada_wing();
        mirror([0, 1, 0])
