        rotate([0, 0, 30])
        cicada_leg();

        translate([0, -10, -5])
        rotate([0, 0, -30])
        mirror([0, 1, 0])
        cicada_leg();

        // Rear legs
        translate([-10, 12, -5])
        rotate([0, 0, 15])
        cicada_leg();

        translate([-10, -12, -5])
        rotate([0, 0, -15])
        mirror([0, 1, 0])
        cicada_leg();
    }
}

