// Coati 3D model - realistic medium complexity
// Designed for 3D printing at ~70mm length

// Main body parameters
body_length = 35;
body_height = 20;
body_width = 18;

// Head parameters
head_length = 15;
head_height = 12;
head_width = 10;

// Tail parameters
tail_segments = 8;
tail_base = 6;
tail_tip = 2;
tail_length = 30;

// Leg parameters
leg_height = 12;
leg_width = 4;
leg_depth = 5;

// Snout parameters
snout_length = 14;
snout_base = 8;
snout_tip = 3;

// Ear parameters
ear_size = [5, 3, 2];

// Scale factor for overall size
scale_factor = 2;

module coati_body() {
    // Main torso
    translate([0, 0, body_height/2])
    scale([1.2, 1, 0.8])
    sphere(d=body_width, $fn=20);

    // Chest
    translate([-body_length/4, 0, body_height/3])
    scale([0.8, 0.9, 0.7])
    sphere(d=body_width*0.9, $fn=20);

    // Rump
    translate([body_length/3, 0, body_height/2.5])
    scale([0.9, 1.1, 0.9])
    sphere(d=body_width*1.1, $fn=20);
}

module coati_head() {
    // Skull
    translate([-body_length/2 - head_length/2, 0, body_height + head_height/2])
    scale([1.3, 1, 1])
    sphere(d=head_width, $fn=20);

    // Snout
    translate([-body_length/2 - head_length - snout_length/2, 0, body_height + head_height/2])
    rotate([0, -15, 0])
    cylinder(h=snout_length, d1=snout_base, d2=snout_tip, $fn=8);

    // Nose tip
    translate([-body_length/2 - head_length - snout_length, 0, body_height + head_height/2 - 2])
    sphere(d=snout_tip*1.2, $fn=8);
}

module coati_ears() {
    // Left ear
    translate([-body_length/2 - head_length/2 - 2, head_width/2, body_height + head_height])
    rotate([0, 0, 30])
    scale(ear_size)
    sphere(d=3, $fn=6);

    // Right ear
    translate([-body_length/2 - head_length/2 - 2, -head_width/2, body_height + head_height])
    rotate([0, 0, -30])
    scale(ear_size)
    sphere(d=3, $fn=6);
}

module coati_tail() {
    // Create tapered tail with segments
    for(i = [0:tail_segments-1]) {
        t = i / (tail_segments - 1);
        segment_length = tail_length / tail_segments;
        diameter = tail_base - (tail_base - tail_tip) * t;
        angle = -20 * sin(i * 45);

        translate([body_length/2 + segment_length * i, 0, body_height/2 + tail_base/2 - diameter/2])
        rotate([0, angle, 5 * sin(i * 30)])
        cylinder(h=segment_length + 1, d=diameter, $fn=8);
    }
}

module coati_legs() {
    // Front legs
    for(side = [-1, 1]) {
        // Left/right positioning
        translate([-body_length/3, side * body_width/2.5, 0])
        rotate([0, 10, 0])
        cylinder(h=leg_height, d=leg_width, $fn=6);

        // Paw
        translate([-body_length/3, side * body_width/2.5, -leg_height])
        sphere(d=leg_width*1.2, $fn=8);
    }

    // Hind legs
    for(side = [-1, 1]) {
        translate([body_length/4, side * body_width/2.3, 0])
        rotate([0, -10, 0])
        cylinder(h=leg_height*1.1, d=leg_width*1.1, $fn=6);

        // Paw
        translate([body_length/4, side * body_width/2.3, -leg_height*1.1])
        sphere(d=leg_width*1.3, $fn=8);
    }
}

module coati_stripes() {
    // Tail rings
    for(i = [1:3]) {
        ring_pos = body_length/2 + tail_length/4 * i;
        color_diameter = tail_base - (tail_base - tail_tip) * (i/4);

        translate([ring_pos, 0, body_height/2])
        rotate([90, 0, 0])
        cylinder(h=1.5, d=color_diameter*1.05, center=true, $fn=20);
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor])
union() {
    coati_body();
    coati_head();
    coati_ears();
    coati_tail();
    coati_legs();
    coati_stripes();
}

