                scale([1.1, 0.7, 0.3])
                    sphere(7, $fn=24);
    }
}

// Lobed fins - distinctive coelacanth feature
module lobed_fin(x_pos, y_pos, z_pos, angle, scale_factor) {
    translate([x_pos, y_pos, z_pos])
        rotate([0, 0, angle])
            scale([scale_factor, scale_factor, scale_factor])
                union() {
                    // Main fin lobe
                    translate([0, 0, 0])
                        scale([3, 1, 0.3])
                            sphere(3, $fn=16);

                    // Fin rays
                    for (i = [-2:1:2]) {
                        translate([2, i*1.5, -0.5])
                            scale([2, 0.3, 0.1])
                                rotate([0, i*10, 0])
                                    cylinder(h=4, r=0.5, $fn=8);
                    }
                }
}

// Tail fin - three-lobed
module tail_fin() {
    translate([-25, 0, 0])
        union() {
            // Upper lobe
            translate([0, 0, 3])
                rotate([30, 0, 0])
                    scale([1, 0.5, 0.2])
