// Conch seashell
// Basic conch shape formed by a logarithmic spiral extruded along a circular path

$fn = 100; // smooth curves

// Parameters
a = 1;      // spiral growth rate
b = 0.2;    // spiral tightness
h = 10;     // shell height scaling
n_turns = 3.5; // number of spiral turns
thickness = 1; // shell thickness

// Helper module: create a single cross-section of the shell
module shell_profile(t) {
    // t goes 0..1 along the spiral
    theta = t * 360 * n_turns;
    r = a * exp(b * theta); // logarithmic spiral

    // Elliptical cross-section that tapers
    scale_factor = 1 - t * 0.8;
    translate([r, 0, h * t])
        rotate([90, 0, 0])
            scale([scale_factor, scale_factor * 0.7, 1])
                circle(d = thickness * (1 + t * 0.5), $fn = 32);
}

// Create the spiral shell using hull and slices
module conch_shell() {
    slices = 50;
    for (i = [0 : slices - 1]) {
        hull() {
            shell_profile(i / slices);
            shell_profile((i + 1) / slices);
        }
    }
}

// Main body
color("LightSalmon")
    conch_shell();

// Add the opening lip that flares outward
module lip() {
    difference() {
        // Flared outer lip
        translate([0, 0, h * 0.9])
            rotate([90, 0, 0])
                cylinder(h = 2, r1 = 3.5, r2 = 4.5, $fn = 64);

        // Cutout to follow shell shape
        translate([0, 0, h * 0.9 - 1])
            rotate([90, 0, 0])
                cylinder(h = 4, r1 = 2.5, r2 = 3.5, $fn = 64);
    }
}

