// Conch shell - medium complexity realistic style
// Uses basic primitives and transformations to create a 3D printable conch

module conch_shell() {
    // Main body parameters
    body_length = 60;
    max_diameter = 25;
    min_diameter = 8;

    // Create main spiral body
    difference() {
        union() {
            // Main spiral structure
            spiral_body();

            // Opening/lip structure
            shell_lip();

            // Spiral ridges
            spiral_ridges();
        }

        // Hollow out the inside
        hollow_interior();
    }

    // Add spire tip
    spire_tip();
}

module spiral_body() {
    $fn = 32;

    // Create spiral using rotated and scaled cylinders
    for (i = [0:5:360]) {
        radius = max_diameter - (max_diameter - min_diameter) * (i / 360);
        height = body_length * (1 - i / 360) * 0.8;
        translate([
            cos(i) * (i / 15),
            sin(i) * (i / 15),
            height
        ]) {
            rotate([90, 0, i]) {
                cylinder(h = 2, r1 = radius, r2 = radius * 0.9);
            }
        }
    }

    // Connect spiral segments with ellipsoids
    for (i = [0:30:360]) {
        radius = max_diameter - (max_diameter - min_diameter) * (i / 360);
        translate([
            cos(i) * (i / 15),
            sin(i) * (i / 15),
            body_length * (1 - i / 360) * 0.8
        ]) {
            scale([1, 1.2, 0.8]) {
                sphere(r = radius * 0.7);
            }
        }
    }
}

module shell_lip() {
    $fn = 32;

    // Outer lip of the aperture
    translate([cos(360) * 24, sin(360) * 24, body_length * 0.2]) {
        rotate([0, -20, 0]) {
            difference() {
                // Main lip shape
                scale([1.5, 1, 1]) {
                    sphere(r = 12);
                }

                // Cut away back
                translate([-15, 0, 0]) {
                    cube([30, 30, 30], center = true);
                }
            }
        }
    }
}

module spiral_ridges() {
    // Add raised ridges along spiral
    for (i = [0:45:360]) {
        for (j = [0:60:360]) {
            radius = max_diameter - (max_diameter - min_diameter) * (j / 360);
            translate([
                cos(j) * (j / 15) + cos(i + j) * radius * 0.6,
                sin(j) * (j / 15) + sin(i + j) * radius * 0.6,
                body_length * (1 - j / 360) * 0.8
            ]) {
                scale([0.5, 0.5, 1.5]) {
                    sphere(r = 1.5);
                }
            }
        }
    }
}

module hollow_interior() {
    // Hollow out shell for 3D printing
    for (i = [0:5:360]) {
        radius = (max_diameter - (max_diameter - min_diameter) * (i / 360)) * 0.7;
        height = body_length * (1 - i / 360) * 0.8;
        translate([
            cos(i) * (i / 15),
            sin(i) * (i / 15),
            height
        ]) {
            rotate([90, 0, i]) {
                cylinder(h = 2.2, r1 = radius, r2 = radius * 0.9);
            }
        }
    }

    // Remove interior from lip
    translate([cos(360) * 24, sin(360) * 24, body_length * 0.2]) {
        rotate([0, -20, 0]) {
            scale([1.2, 0.8, 0.8]) {
                sphere(r = 10);
            }
        }
    }
}

module spire_tip() {
    // Pointed spire at top
    translate([0, 0, body_length * 0.8]) {
        rotate([0, 180, 0]) {
            cylinder(h1 = 8, h2 = 0, r1 = 6, r2 = 0, $fn = 6);
        }
    }
}

// Create the conch shell
conch_shell();

