// Cockroach.scad - Realistic style, medium complexity

// Overall size: ~80mm length
$fn = 30;

module cockroach() {
    // Main body segments
    union() {
        // Thorax (middle segment)
        color("brown")
        translate([0, 0, 5])
        rotate([0, 90, 0])
        scale([1.2, 1, 0.8])
        cylinder(h = 25, r1 = 8, r2 = 10);

        // Abdomen (largest segment)
        color("darkbrown")
        translate([-20, 0, 6])
        rotate([0, 90, 0])
        scale([1, 1, 0.7])
        cylinder(h = 30, r1 = 10, r2 = 8);

        // Head
        color("brown")
        translate([15, 0, 5])
        rotate([0, 90, 0])
        scale([1, 0.9, 0.9])
        cylinder(h = 12, r1 = 7, r2 = 5);

        // Wings (elytra)
        left_wing();
        right_wing();

        // Legs
        left_legs();
        right_legs();

        // Antennae
        left_antenna();
        right_antenna();
    }
}

module left_wing() {
    // Left wing as overlapping plates
    color("brown")
    translate([-5, -6, 10])
    rotate([0, 10, -15])
    scale([1.2, 0.5, 0.1])
    cube([25, 12, 1]);
}

module right_wing() {
    // Right wing (mirror of left)
    mirror([0, 1, 0])
    left_wing();
}

module leg_pair(offset, angle) {
    // Single pair of legs
    translate([offset, 0, 0])
    rotate([0, 0, angle])
    union() {
        // Coxa (first segment)
        translate([0, -4, 4])
        rotate([0, 90, 0])
        cylinder(h = 8, r = 2);

        // Femur
        translate([0, -8, 2])
        rotate([0, 45, 0])
        cylinder(h = 12, r = 1.5);

        // Tibia
        translate([0, -16, 0])
        rotate([0, 30, 0])
        cylinder(h = 10, r = 1);

        // Tarsus segments
        for(i = [1:3]) {
            translate([0, -20 - i*3, -0.5*i])
            rotate([0, 20, 0])
            cylinder(h = 3, r = 0.8);
        }
    }
}

module left_legs() {
    // Three legs on left side
    leg_pair(10, -45);   // Front
    leg_pair(0, -90);    // Middle
    leg_pair(-15, -135); // Rear
}

module right_legs() {
    // Mirror legs to right side
    mirror([0, 1, 0]) {
        leg_pair(10, 45);
        leg_pair(0, 90);
        leg_pair(-15, 135);
    }
}

module left_antenna() {
