    color("black")
    translate([18, -3, 8])
    rotate([0, 0, 30])
    for(i = [0:5]) {
        translate([i*2, -i*0.5, 0])
        rotate([0, 0, -10])
        cylinder(h = 3, r = 0.3);
    }
}

module right_antenna() {
    // Right antenna
    mirror([0, 1, 0])
    left_antenna();
}

